import java.util.Scanner;
public class EvenOdd {
	

	public static void main(String[] args) {
		Scanner s = new Scanner (System.in);
		 System.out.println("Enter Number:");
		 int n=s.nextInt();
	for(int j=1; j<=10; j++) {
				
				  System.out.println(n + " * " + j + " " + " = " + n * j);
			  
			  }
	}
	}	

			
	

			  
			  
			 

	
	
